                    subquery += string.Format("and cus.Market in ({0}) ", market);
                }
                if (!string.IsNullOrWhiteSpace(servicetype) && servicetype != "-1" && servicetype != "'null'")
                {
                    subquery += string.Format(" and cus.Type in ({0}) ", servicetype);
