                }
                leadsourcequery = leadsourcequery.Remove(leadsourcequery.Length - 1, 1);
            }
            if (!string.IsNullOrWhiteSpace(filter.LeadSourceText) && filter.LeadSourceText != "-1" && filter.LeadSourceText != "undefined")
