            }
            #endregion
            #region leadstatus
            if (!string.IsNullOrEmpty(filter.LeadStatusText) && filter.LeadStatusText != "-1" && filter.LeadStatusText != "undefined" && filter.LeadStatusText != null)
            {
                var array1 = filter.LeadStatusText.Split(",");
