                        from Invoice inv
                        LEFT JOIN Customer cus on cus.CustomerId=inv.CustomerId
                        where inv.CustomerId='{0}' And (inv.Status='Open' or inv.Status='Partial')
                        {1}
                        {2}
                        ";
