            //            NotificationOnly = true,
            //        };
            //        _Util.Facade.TicketFacade.InsertTicketUser(TicketUser);
            //    }
            //}

            #endregion

            if (IsNewTicket)
            {
                #region Send Ticket Created Email


                string ToEmailList = "";
                //List<Guid> AllUser = new List<Guid>();
                //if (Assigned != null)
                //{
                //    AllUser.AddRange(Assigned);
