                        var comname = _Util.Facade.CompanyFacade.GetCompanyByComapnyId(currentLoggedIn.CompanyId.Value).CompanyName.ReplaceSpecialChar();
                        var FtempFolderName = string.Format(tempFolder, comname) + ci.CusId + "Drawing";
