        #region Make address 
        //[street][streetType][appartment]
        //[zip][city],[state]
        private string MakeAddress(string street, string streetType, string appartment, string city, string state, string zipCode)
        {
            string address = "";
            if (!string.IsNullOrWhiteSpace(street))
            {
                if (street != "-1")
                {
                    address += street;
                }

            }
            if (!string.IsNullOrWhiteSpace(streetType))
            {
                if (streetType != "-1")
                {
                    if (!string.IsNullOrWhiteSpace(address))
                    {
                        address += " " + streetType;
                    }
                    else
                    {
                        address += streetType;
                    }
                }

            }
            if (!string.IsNullOrWhiteSpace(appartment))
            {
                if (appartment != "-1")
                {
                    if (!string.IsNullOrWhiteSpace(address))
                    {
                        address += " " + appartment;
                    }
                    else
                    {
                        address += appartment;
                    }
                }

            }
            if (!string.IsNullOrWhiteSpace(address))
            {
                address += " <br> ";
            }

            if (!string.IsNullOrWhiteSpace(city))
            {
                if (city != "-1")
                {
                    if (!string.IsNullOrWhiteSpace(address))
                    {
                        address += " " + city;
                    }
                    else
                    {
                        address += city;
                    }

                }
            }
            if (!string.IsNullOrWhiteSpace(state))
            {
                if (state != "-1")
                {
                    if (!string.IsNullOrWhiteSpace(address))
                    {
                        address += ", " + state;
                    }
                    else
                    {
                        address += state;
                    }
                }

            }
            if (!string.IsNullOrWhiteSpace(zipCode))
            {
                if (zipCode != "-1")
                {
                    address += " " + zipCode;
                }
            }

            return address.TrimEnd(',');
        }
        #endregion


