            if (Model.ListCustomerAppointmentEquipment.Count == 0)
            {
                message.Add("You have not selected any Equipment. ");
            }
            if (Model.ListPaymentInfoCustomer.Count == 0)
            {
                message.Add("You have not selected any payment method. ");
            }
