            {
                string[] spurl = DESEncryptionDecryption.DecryptCipherTextToPlainText(url).Split('#');
                if (spurl.Length == 11)
                {
                    leadid = Convert.ToInt32(spurl[0]);
                    if (!string.IsNullOrWhiteSpace(spurl[1]))
                    {
                        agreementtempid = Convert.ToInt32(spurl[1]);
                    }
