                tw = new XmlTextWriter(sw);
                serializer.Serialize(tw, o);
            }
            catch (Exception ex)
            {
                //logger.Error(ex);
                //Handle Exception Code
            }
            finally
            {
                sw.Close();
                if (tw != null)
